import React, { useState } from 'react';
import Terminal from './Terminal';

const Window = ({ title, children, onClose }) => {
  const [position, setPosition] = useState({ x: 50, y: 50 });
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  const startDragging = (e) => {
    setIsDragging(true);
    setDragOffset({
      x: e.clientX - position.x,
      y: e.clientY - position.y
    });
  };

  const stopDragging = () => {
    setIsDragging(false);
  };

  const drag = (e) => {
    if (isDragging) {
      setPosition({
        x: e.clientX - dragOffset.x,
        y: e.clientY - dragOffset.y
      });
    }
  };

  return (
    <div 
      className="absolute bg-gray-800 border border-gray-700 shadow-lg"
      style={{ left: `${position.x}px`, top: `${position.y}px`, width: '500px', height: '400px' }}
      onMouseMove={drag}
      onMouseUp={stopDragging}
      onMouseLeave={stopDragging}
    >
      <div 
        className="bg-gray-700 text-white p-2 cursor-move flex justify-between items-center"
        onMouseDown={startDragging}
      >
        <span>{title}</span>
        <button onClick={onClose} className="focus:outline-none">Ã—</button>
      </div>
      <div className="p-4 h-5/6 overflow-auto">
        {children}
      </div>
    </div>
  );
};

const Desktop = () => {
  const [windows, setWindows] = useState([]);

  const openWindow = (app) => {
    setWindows([...windows, app]);
  };

  const closeWindow = (index) => {
    setWindows(windows.filter((_, i) => i !== index));
  };

  return (
    <div className="h-screen bg-gray-900 overflow-hidden relative">
      <div className="p-4 grid grid-cols-6 gap-4">
        <button onClick={() => openWindow({ title: 'Terminal', content: <Terminal /> })} className="text-white">
          Terminal
        </button>
        {/* Add more app buttons here */}
      </div>
      {windows.map((window, index) => (
        <Window key={index} title={window.title} onClose={() => closeWindow(index)}>
          {window.content}
        </Window>
      ))}
    </div>
  );
};

export default Desktop;
